#Perform differential expression analysis
for i in meta_SRP028336/*tsv;do p=`echo $i|cut -d "/" -f 2|perl -pe 's/.samples.tsv//'`;g1=`cat $i|cut -f2|grep -v condition|sort|uniq|head -1`;g2=`cat $i|cut -f2|grep -v condition|sort|uniq|tail -n1`;echo "snakemake --cores all -F --use-singularity --singularity-args \"-B analysis/src/dge_functions_data:analysis/src/dge_functions_data\" -s Snakefile_dge.py $p/figures_tables.${g1}.${g2}.out/DESeq2.exon_counts_subsampled.DESeq2.${g1}.vs.${g2}.txt"|cat template.pbs - >pbs/$p.pbs;qsub pbs/$p.pbs;done
for i in meta_E-MTAB-7247/*tsv;do p=`echo $i|cut -d "/" -f 2|perl -pe 's/.samples.tsv//'`;g1=`cat $i|cut -f2|grep -v condition|sort|uniq|head -1`;g2=`cat $i|cut -f2|grep -v condition|sort|uniq|tail -n1`;echo "snakemake --cores all -F --use-singularity --singularity-args \"-B analysis/src/dge_functions_data:analysis/src/dge_functions_data\" -s Snakefile_dge.py $p/figures_tables.${g1}.${g2}.out/DESeq2.exon_counts_subsampled.DESeq2.${g1}.vs.${g2}.txt"|cat template.pbs - >pbs/$p.pbs;qsub pbs/$p.pbs;done
